pragma solidity ^0.4.24;

contract CafeArticle {


    function totalSupply() view public returns (uint256);
    function balanceOf(address _owner) public view returns (uint256);
    function transfer(address _from,address _to,uint256 _id) internal ;
    function createArticle(string _name,address _to,bool _approve) external;
    function transferToOwner (address _from,uint256 _id) external;
    function ownerOf(uint256 _id) external view returns (address);
}


contract BuyToken {

    CafeArticle public Cafe;
    address public owner;
    bool internal configOwner;

    event transferEther(address _from,uint256 _value);

    constructor (address _tokenAdd) public {
        owner = msg.sender;
        Cafe = CafeArticle(_tokenAdd);
    }

    modifier isOwner {
        require(msg.sender == owner);
        _;
    }

    function buyArticle () public payable {
        require(msg.value > 0);
        owner.transfer(msg.value);
        emit transferEther(msg.sender,msg.value);
        bool approve = true;
        if (msg.value == 0.001 ether)
        Cafe.createArticle("Normal",msg.sender,approve);
        if (msg.value == 0.002 ether)
        Cafe.createArticle("VIP",msg.sender,approve);
        emit transferEther(msg.sender,msg.value);
    }
    function payByToken (uint256 _id) public {
        require(Cafe.ownerOf(_id) == msg.sender);
        Cafe.transferToOwner(msg.sender,_id);
    }

}
