pragma solidity ^0.4.24;

contract ERC721 {
    // Required methods
    function totalSupply() public view returns (uint256 total);
    function balanceOf(address _owner) public view returns (uint256 balance);
    function ownerOf(uint256 _tokenId) external view returns (address owner);
    function approve(address _to, uint256 _tokenId) external;
    function transfer(address _to, uint256 _tokenId) external;
    function transferFrom(address _from, address _to, uint256 _tokenId) external;

    // Events
    event Transfer(address from, address to, uint256 tokenId);
    event Approval(address owner, address approved, uint256 tokenId);

    // Optional
    // function name() public view returns (string name);
    // function symbol() public view returns (string symbol);
    // function tokensOfOwner(address _owner) external view returns (uint256[] tokenIds);
    // function tokenMetadata(uint256 _tokenId, string _preferredTransport) public view returns (string infoUrl);

    // ERC-165 Compatibility (https://github.com/ethereum/EIPs/issues/165)
    function supportsInterface(bytes4 _interfaceID) external view returns (bool);
}

contract CafeArticle {

    address public storekeeper;

    struct Article {
        string name;
    }

    string internal name;
    string internal symbol;

    mapping(string => uint256)  discount;

    mapping(uint256 => address)  articleOwner;

    mapping(uint256 => address)  ownerOfArticle;

    mapping(address => uint256)  ownedArticleCount;

    mapping(uint256 => address)  approvedArticleTo;

    event Transfer(address from, address to, uint256 tokenId);

    Article[] public articles;

    modifier isStoreKeeper {
        require(msg.sender == storekeeper);
        _;
    }

    constructor (string _name,string _symbol) public {
         storekeeper = msg.sender;
         name = _name;
         symbol = _symbol;
    }

    function totalSupply() view public returns (uint256) {
        return articles.length;
    }

    function balanceOf(address _owner) public view returns (uint256 _balance) {
        return ownedArticleCount[_owner];
    }

    function transfer(address _from,address _to,uint256 _id) internal {
        ownedArticleCount[_to] += 1;
        articleOwner[_id] = _to;
        if(_from != address(0)){
            ownedArticleCount[_from] -= 1;
        }
    }
    function createArticle(string _name,address _to,bool _approve) external  {
        require(_approve);
        uint256 id = articles.length;
        Article memory newArticle = Article(_name);
        articles.push(newArticle);
        articleOwner[id] = address (0);
        transfer(address (0),_to,id);
    }

    function ownerCreateArticle(string _name,address _to) public isStoreKeeper returns(bool _success) {
        uint256 id = articles.length;
        Article memory newArticle = Article(_name);
        articles.push(newArticle);
        articleOwner[id] = address (0);
        transfer(address (0),_to,id);
        return true;
    }

    function transferToOwner (address _from,uint256 _id) external {
        transfer(_from, storekeeper,_id);
    }
    // function approve(address _to, uint256 _id) public {
    //     address owner = ownerOfArticle[_id];
    //     require(msg.sender == owner);
    //     require(_to != owner);
    //     approvedArticleTo[_id] = _to;
    // }

    // function exists(uint256 _id) public view returns (bool) {
    //     address owner = articleOwner[_id];
    //     return owner != address(0);
    // }

    function ownerOf(uint256 _id) external view returns (address) {
        return articleOwner[_id];
    }

    // function clearApproval(address _owner, uint256 _id) internal {
    //     require(articleOwner[_id] == _owner);
    //     if (approvedArticleTo[_id] != address(0)) {
    //         approvedArticleTo[_id] = address(0);
    //     }
    // }

    // function getApproved(uint256 _id) public view returns (address) {
    //     return approvedArticleTo[_id];
    // }
}
contract buyToken {

    CafeArticle public Cafe;
    address public owner;
    bool internal configOwner;

    event transferEther(address _from,uint256 _value);

    constructor (address _tokenAdd) public {
        owner = msg.sender;
        Cafe = CafeArticle(_tokenAdd);
    }

    modifier isOwner {
        require(msg.sender == owner);
        _;
    }

    function buyCrticle (string _name) public payable {
        require(msg.value == 0.001 ether);
        owner.transfer(msg.value);
        emit transferEther(msg.sender,msg.value);
        bool approve = true;
        Cafe.createArticle(_name,msg.sender,approve);
    }
    function payByToken (uint256 _id) public {
        require(Cafe.ownerOf(_id) == msg.sender);
        Cafe.transferToOwner(msg.sender,_id);
    }

}
